mapper.insert:
INSERT INTO employee
(
  employee_id
, employee_name
, employee_password
, request_date
, modify_date
, status
)
VALUES
(
  #{employeeId}
, #{employeeName}
, #{employeePassword}
, #{requestDate}
, #{modifyDate}
, #{status}
)

    CALL SCOPE_IDENTITY()

INSERT INTO employee_history
(
  employee_seq
, employee_id
, employee_name
, employee_password
, request_date
, modify_date
, status
)
VALUES
(
  #{employeeSeq}
, #{employeeId}
, #{employeeName}
, #{employeePassword}
, #{requestDate}
, #{modifyDate}
, #{status}
)

mapper.update:
UPDATE employee
SET modify_date = #{modifyDate}

  , employee_name = #{employeeName}


  , employee_password = #{employeePassword}


  , status = #{status}

WHERE employee_seq = #{employeeSeq}
